settings.custom_liquid:
{%- assign video_links = "YOUR_VIDEO_LINK_1,YOUR_VIDEO_LINK_2,https://youtu.be/yODjW9_-fww?si=h3iIZqUFBHhbOGLo,YOUR_VIDEO_LINK_4,YOUR_VIDEO_LINK_5,YOUR_VIDEO_LINK_6" | split: ',' -%}
{%- assign titles = "Dinosaurs,Birds,Beetles,Fish,Garden,Butterfly-Moths" | split: ',' -%}

<div class="video-grid">
  {%- for i in (0..5) -%}
    <div class="video-item">
      <div class="video-title">
        <h3>{{ titles[i] }}</h3>
      </div>
      <div class="video-wrapper">
        <iframe src="https://www.youtube.com/embed/{{ video_links[i] }}" frameborder="0" allowfullscreen></iframe>
      </div>
    </div>
  {%- endfor -%}
</div>

<style>
/* Styling for the video grid */
.video-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 20px;
}

/* Styling for each video item */
.video-item {
  border: 2px solid black;
  border-radius: 10px;
  overflow: hidden;
}

/* Title styling */
.video-title {
  padding: 10px;
  text-align: center;
}

/* Video wrapper styling */
.video-wrapper {
  position: relative;
  padding-bottom: 100%; /* Maintain 1:1 aspect ratio */
}

.video-wrapper iframe {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}
</style>
